/** 
 * 登陆
 */
function login(){
    password = document.getElementById("userpassword").value;
    web3.eth.getAccounts().then(function(accounts){
        web3.eth.personal.unlockAccount(accounts[0],password).then(function(value){
            // 解锁账户 - 成功
            console.log("unlockAccount");
            loginIn=true;
            window.location.replace("./main.html");
        }, function(reason) {
            // 解锁账户 - 失败
            console.log("unlockAccount failed",reason);
            alert("Wrong Password!")
            return false;
        });
    }, function(reason) {
        // 获取账户 - 失败
        console.log("Fail to get accounts list",reason);
        alert("Please check you internet!")
        return false;
    });
}